var toggleHandler=function(a){var a=a;var b=$(a).find("input");var c=function(){if(b.eq(0).is(":checked")){$(a).removeClass("toggle-off")}else{$(a).addClass("toggle-off")}};c();b.eq(0).click(function(){$(a).toggleClass("toggle-off")});b.eq(1).click(function(){$(a).toggleClass("toggle-off")})};$(document).ready(function(){$(".toggle").each(function(b,a){toggleHandler(a)})});