function CustomHtml(options){this.button=document.createElement("button");this.button.className="medium-editor-action";if(this.button.innerText){this.button.innerText=options.buttonText||"</>"}else{this.button.textContent=options.buttonText||"</>"}this.button.onclick=this.onClick.bind(this);this.options=options;this.insertHtmlAtCaret=function(html){var sel,range;if(window.getSelection){sel=window.getSelection();if(sel.getRangeAt&&sel.rangeCount){range=sel.getRangeAt(0);range.deleteContents();var el=document.createElement("div");el.innerHTML=html;var frag=document.createDocumentFragment(),node,lastNode;while(node=el.firstChild){lastNode=frag.appendChild(node)}range.insertNode(frag);if(lastNode){range=range.cloneRange();range.setStartAfter(lastNode);range.collapse(true);sel.removeAllRanges();sel.addRange(range)}}}else if(document.selection&&document.selection.type!="Control"){document.selection.createRange().pasteHTML(html)}}}CustomHtml.prototype.onClick=function(){this.insertHtmlAtCaret(this.options.htmlToInsert)};CustomHtml.prototype.getButton=function(){return this.button};